      styles: ["\n\n.hero-section[_ngcontent-%COMP%] {\n  background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), \n              url('https://images.unsplash.com/photo-1589829545856-d10d557cf95f?ixlib=rb-4.0.3&auto=format&fit=crop&w=2070&q=80') center center/cover no-repeat;\n  position: relative;\n  min-height: 100vh;\n  display: flex;\n  align-items: center;\n  overflow: hidden;\n}\n\n.hero-overlay[_ngcontent-%COMP%] {\n  position: absolute;\n  top: 0;\n  left: 0;\n  right: 0;\n  bottom: 0;\n  background-color: rgba(0, 0, 0, 0.4);\n  z-index: 1;\n}\n\n.hero-content[_ngcontent-%COMP%] {\n  position: relative;\n  z-index: 2;\n  text-align: center;\n  max-width: 800px;\n  margin: 0 auto;\n  padding: 0 20px;\n}\n\n.hero-title[_ngcontent-%COMP%] {\n  margin-bottom: 15px;\n}\n\n.title-main[_ngcontent-%COMP%] {\n  font-size: 3.5rem;\n  font-weight: 700;\n  color: white;\n  line-height: 1.2;\n  margin-bottom: 5px;\n  text-shadow: 1px 1px 4px rgba(0, 0, 0, 0.7);\n}\n\n.title-subtitle[_ngcontent-%COMP%] {\n  font-size: 1.8rem;\n  font-weight: 600;\n  color: #FFD700;\n  line-height: 1.3;\n  text-shadow: 1px 1px 4px rgba(0, 0, 0, 0.7);\n}\n\n.hero-tagline[_ngcontent-%COMP%] {\n  font-size: 1.3rem;\n  color: white;\n  margin-bottom: 25px;\n  font-weight: 600;\n  text-shadow: 1px 1px 4px rgba(0, 0, 0, 0.6);\n}\n\n.hero-description[_ngcontent-%COMP%] {\n  font-size: 1.1rem;\n  color: rgba(255, 255, 255, 0.9);\n  line-height: 1.7;\n  margin-bottom: 35px;\n  text-shadow: 1px 1px 4px rgba(0, 0, 0, 0.5);\n}\n\n.hero-buttons[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: center;\n  gap: 15px;\n  flex-wrap: wrap;\n  margin-bottom: 30px;\n}\n\n.btn-hero[_ngcontent-%COMP%] {\n  padding: 12px 30px;\n  font-size: 1rem;\n  font-weight: 600;\n  border-radius: 30px;\n  text-decoration: none;\n  transition: all 0.3s ease;\n  border: none;\n  text-transform: uppercase;\n  letter-spacing: 0.5px;\n  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);\n}\n\n.btn-primary.btn-hero[