import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="container-fluid">
      <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
        <div class="container">
          <a class="navbar-brand" href="#">
            SGA - SAGA Abogados
          </a>
        </div>
      </nav>
      
      <main class="container mt-4">
        <h1>Bienvenido al Sistema SGA</h1>
        <p>Sistema de Gestión de Abogados - SAGA Abogados y Asociados</p>
      </main>
      
      <footer class="bg-dark text-white text-center py-3 mt-5">
        <div class="container">
          <p>&copy; 2024 SAGA Abogados y Asociados. Todos los derechos reservados.</p>
        </div>
      </footer>
    </div>
  `,
  styles: [`
    .navbar-brand {
      font-size: 1.5rem;
      font-weight: 700;
    }
    
    main {
      min-height: calc(100vh - 140px);
    }
  `]
})
export class AppComponent {
  title = 'SGA - Sistema de Gestión de Abogados';
}
